import React, { createContext, useContext, useState, useEffect } from 'react'
import { Application, ApplicationStatus, User, EmailNotification, Dashboard } from '@/types'

interface DataContextType {
  applications: Application[]
  emailNotifications: EmailNotification[]
  dashboard: Dashboard
  createApplication: (application: Omit<Application, 'id' | 'createdAt' | 'updatedAt'>) => void
  updateApplicationStatus: (id: string, status: ApplicationStatus) => void
  updateApplication: (id: string, updates: Partial<Application>) => void
  sendEmailNotification: (notification: Omit<EmailNotification, 'id' | 'sentAt'>) => void
  simulateAIVerification: (applicationId: string) => Promise<void>
  calculateScore: (application: Application) => number
}

const DataContext = createContext<DataContextType | undefined>(undefined)

export const useData = () => {
  const context = useContext(DataContext)
  if (context === undefined) {
    throw new Error('useData must be used within a DataProvider')
  }
  return context
}

// Mock data for demonstration
const MOCK_APPLICATIONS: Application[] = [
  {
    id: '1',
    studentId: '1',
    studentName: 'John Student',
    department: 'Computer Science',
    rollNumber: 'CS001',
    class: 'Final Year',
    status: 'Submitted',
    submittedAt: new Date('2024-01-15'),
    academicPerformance: [
      {
        semester: 'Sem 1',
        gpa: 3.8,
        subjects: [
          { subject: 'Data Structures', score: 92, maxScore: 100 },
          { subject: 'Algorithms', score: 88, maxScore: 100 }
        ]
      },
      {
        semester: 'Sem 2',
        gpa: 3.9,
        subjects: [
          { subject: 'Database Systems', score: 95, maxScore: 100 },
          { subject: 'Software Engineering', score: 90, maxScore: 100 }
        ]
      }
    ],
    extracurricularActivities: [
      {
        id: 'a1',
        type: 'Academic',
        name: 'Coding Competition Winner',
        description: 'Won 1st place in inter-college coding competition',
        points: 20,
        date: new Date('2024-01-10')
      },
      {
        id: 'a2',
        type: 'Leadership',
        name: 'Class Representative',
        description: 'Served as class representative for 2 years',
        points: 15,
        date: new Date('2024-01-01')
      }
    ],
    disciplineRecord: [],
    createdAt: new Date('2024-01-15'),
    updatedAt: new Date('2024-01-15')
  }
]

export const DataProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [applications, setApplications] = useState<Application[]>(MOCK_APPLICATIONS)
  const [emailNotifications, setEmailNotifications] = useState<EmailNotification[]>([])

  const createApplication = (applicationData: Omit<Application, 'id' | 'createdAt' | 'updatedAt'>) => {
    const newApplication: Application = {
      ...applicationData,
      id: Date.now().toString(),
      createdAt: new Date(),
      updatedAt: new Date()
    }
    setApplications(prev => [...prev, newApplication])
  }

  const updateApplicationStatus = (id: string, status: ApplicationStatus) => {
    setApplications(prev => 
      prev.map(app => 
        app.id === id 
          ? { ...app, status, updatedAt: new Date() }
          : app
      )
    )

    // Trigger email notification based on status change
    const application = applications.find(app => app.id === id)
    if (application) {
      triggerStatusChangeEmail(application, status)
    }
  }

  const updateApplication = (id: string, updates: Partial<Application>) => {
    setApplications(prev => 
      prev.map(app => 
        app.id === id 
          ? { ...app, ...updates, updatedAt: new Date() }
          : app
      )
    )
  }

  const sendEmailNotification = (notificationData: Omit<EmailNotification, 'id' | 'sentAt'>) => {
    const notification: EmailNotification = {
      ...notificationData,
      id: Date.now().toString(),
      sentAt: new Date(),
      status: 'Sent'
    }
    setEmailNotifications(prev => [...prev, notification])
  }

  const simulateAIVerification = async (applicationId: string): Promise<void> => {
    // Simulate AI processing time
    await new Promise(resolve => setTimeout(resolve, 2000))

    // Mock AI verification result
    const verificationResult = {
      verified: Math.random() > 0.2, // 80% pass rate
      plagiarismScore: Math.floor(Math.random() * 20),
      consistencyScore: Math.floor(Math.random() * 20) + 80,
      feedback: 'AI verification completed. Document authenticity confirmed.',
      timestamp: new Date()
    }

    updateApplication(applicationId, {
      aiVerificationResult: verificationResult,
      status: verificationResult.verified ? 'AI Verified' : 'Verification Failed'
    })
  }

  const calculateScore = (application: Application): number => {
    // Academic Performance (50%)
    const avgGPA = application.academicPerformance.reduce((sum, record) => sum + record.gpa, 0) / application.academicPerformance.length
    const academicScore = (avgGPA / 4.0) * 50

    // Extracurricular Activities (20%)
    const activityScore = Math.min(application.extracurricularActivities.reduce((sum, activity) => sum + activity.points, 0) * 0.2, 20)

    // Interview Scores (20% total - 10% each)
    const deptInterviewScore = application.departmentInterview?.score ? (application.departmentInterview.score / 100) * 10 : 0
    const finalInterviewScore = application.finalInterview?.score ? (application.finalInterview.score / 100) * 10 : 0

    // Discipline Deduction (10%)
    const disciplineDeduction = application.disciplineRecord.reduce((sum, record) => sum + record.pointsDeducted, 0)

    const totalScore = academicScore + activityScore + deptInterviewScore + finalInterviewScore - disciplineDeduction

    return Math.max(0, Math.min(100, totalScore))
  }

  const triggerStatusChangeEmail = (application: Application, newStatus: ApplicationStatus) => {
    const emailTemplates = {
      'Submitted': {
        subject: 'Application Submitted Successfully',
        content: `Dear ${application.studentName}, your application for the Best Student Award has been submitted successfully.`
      },
      'AI Verified': {
        subject: 'Application Verified',
        content: `Dear ${application.studentName}, your application has been verified by our AI system and is now under review.`
      },
      'Verification Failed': {
        subject: 'Application Verification Failed',
        content: `Dear ${application.studentName}, unfortunately your application failed our verification process. Please contact the administrator.`
      },
      'Dept Shortlisted': {
        subject: 'Shortlisted for Department Interview',
        content: `Congratulations ${application.studentName}! You have been shortlisted for the department interview.`
      }
    }

    const template = emailTemplates[newStatus as keyof typeof emailTemplates]
    if (template) {
      sendEmailNotification({
        recipient: `${application.studentName} <${application.studentName.toLowerCase().replace(' ', '.')}@mgx.edu>`,
        subject: template.subject,
        content: template.content,
        trigger: `Status changed to ${newStatus}`,
        status: 'Sent'
      })
    }
  }

  // Calculate dashboard data
  const dashboard: Dashboard = {
    totalApplications: applications.length,
    byStatus: applications.reduce((acc, app) => {
      acc[app.status] = (acc[app.status] || 0) + 1
      return acc
    }, {} as Record<ApplicationStatus, number>),
    byDepartment: applications.reduce((acc, app) => {
      acc[app.department] = (acc[app.department] || 0) + 1
      return acc
    }, {} as Record<string, number>),
    averageScore: applications.reduce((sum, app) => sum + calculateScore(app), 0) / applications.length,
    topScorers: applications
      .map(app => ({ ...app, totalScore: calculateScore(app) }))
      .sort((a, b) => b.totalScore! - a.totalScore!)
      .slice(0, 5)
  }

  return (
    <DataContext.Provider value={{
      applications,
      emailNotifications,
      dashboard,
      createApplication,
      updateApplicationStatus,
      updateApplication,
      sendEmailNotification,
      simulateAIVerification,
      calculateScore
    }}>
      {children}
    </DataContext.Provider>
  )
}
</content>
